import {useContext, useState} from 'react';
import axios from 'axios';
import './PostCreate.css';
import { AppContext } from '../../exportStore';

const PostCreate = () => {
    const {state, dispatch} = useContext(AppContext);
    const [posts, setPosts] = useState('');

    const onChangeHandler = (e) => {
        setPosts(e.target.value);
    } 
    return(
        <>
            {state.user &&
                <div class='container-p15 mt-3'>
                    <form class='form'>
                        <div class='form-group'>
                            <textarea className='form-control' id='content' name='content' rows='3' placeholder="What's happening?"></textarea>
                        </div>
                        <div class='form-group text-right'>
                            <button type='submit' className='btn'>
                            <i class="fab fa-twitter" style={{color:"#25dbf6"}}></i> Tweet</button>
                        </div>
                    </form>
                </div>
            }
        </>
    )
}
export default PostCreate;
