import { 
    CURRENT_USER, 
    GET_ALL_POSTS, 
    CREATE_ONE_POST, 
    UPDATE_ONE_POST, 
    DELETE_ONE_POST } 
from "../actions/actions";

export const initialState = {
    user: null,
    posts: []
  }

//actions payloads
export const currentUser = payload => ({
    type: CURRENT_USER, payload
});
export const getAllPost = payload => ({
    type: GET_ALL_POSTS, payload
});
export const createOnePost = payload => ({
    type: CREATE_ONE_POST, payload
})
export const updateOnePost = payload => ({
    type: UPDATE_ONE_POST, payload
})
export const deleteOnePost = payload => ({
    type: DELETE_ONE_POST, payload
});

const reducer = (state,action) => {
    switch(action.type){
        case CURRENT_USER:
            return {...state, user: action.payload};
        case GET_ALL_POSTS:
            return {...state, posts: action.payload};
        case CREATE_ONE_POST:
            return {...state, posts: [...state.posts, action.payload]}
        case UPDATE_ONE_POST:
            return {
                ...state,
                posts: state.posts.map(post => {
                    if(post._id === action.payload._id){
                        return {...post, ...action.payload};
                    }
                    return post;
                })
            }
        case DELETE_ONE_POST:
            return {
                ...state,
                posts: state.posts.filter(post => post._id !== action.payload)
            }
        default:
            return state;
    }
}
export default reducer;
