import './Register.css';
import {useState, useContext} from 'react';
import {useNavigate} from 'react-router-dom'
import axios from 'axios';
import {AppContext, actionpayloads} from '../../exportStore';

const Register = () => {
    const {dispatch} = useContext(AppContext);
    const [userInput, setUserInput] = useState({username: "", email: "", password: ""});
    const [errorMessage, setErrorMessage] = useState(null);
    const history = useNavigate();
    const onChangeHandler = (e) => {
        setUserInput({...userInput, [e.target.name]: e.target.value});
    }
    const onSubmitHandler = async (e) => {
        try{
            e.preventDefault();
            const option = {
                method: 'POST',
                url: '/api/v1/users/register',
                data: userInput
            }
            const response = await axios(option);
            const {token, user} = response.data.data;
            localStorage.setItem('token', token);
            dispatch(actionpayloads.currentUser(user.username));
            history('/');
        }catch(err){
            setErrorMessage(err.response.data.message);
        }
    }
    return (
        <>
            <div className="form-login mt-5 mb-5">
                <div className="form-login-container">
                    <h5 className='login-text-header text-center'>Register Account</h5>
                    <form className='form-login-form' autoComplete='off' onSubmit={onSubmitHandler}>
                        <div className='form-group'>
                            {errorMessage && Array.isArray(errorMessage) ? 
                                errorMessage.map((err, index) => 
                                    <div key={index} className='error-message text-center'>
                                        {`Error: ${err}`}
                                    </div>) : 
                                <div className='error-message text-center'>
                                    {errorMessage ? `Error: ${errorMessage}` : ''}
                                </div>
                            }
                        </div>
                        <div className='form-group'>
                            <input type='text' className='form-control' id='username' name='username' placeholder='Name' value={userInput.username} onChange={onChangeHandler}/>
                        </div>
                        <div className='form-group mt-4'>
                            <input type='email' className='form-control' id='email' name='email' placeholder='Email'value={userInput.email} onChange={onChangeHandler}/>
                        </div>
                        <div className='form-group mt-4'>
                            <input type='password' className='form-control' id='password' name='password' placeholder='Password' value={userInput.password} onChange={onChangeHandler}/>
                        </div>
                        <div className='form-group text-right mt-4'>
                            <button type='submit' className='btn-custom mt-2 btn-center'>Register</button>
                        </div>
                    </form>
                </div>
            </div>
        </>
    )
}
export default Register;
