import './Login.css';
import axios from 'axios';
import AppContext from '../../Context/AppContext';
import {useNavigate} from 'react-router-dom';
import {useContext, useState} from 'react';
import {CURRENT_USER} from '../../actions/actions';

const Login = () => {
    const {dispatch} = useContext(AppContext);
    const [userInput, setUserInput] = useState({email: "", password: ""});
    const [errorMessage, setErrorMessage] = useState(null);
    const history = useNavigate();
    const onChangeHandler = (e) => {
        setUserInput({...userInput, [e.target.name]: e.target.value})
    }
    const onSubmitHandler = async (e) => {
        try{
            e.preventDefault();
            const option = {
                method: 'POST',
                url: '/api/v1/users/login',
                data: userInput
            }
            const response = await axios(option);
            const {token, user} = response.data;
            localStorage.setItem('token', token);
            dispatch({type: CURRENT_USER, payload: user});
            console.log(response.data);
            // history('/');
        }catch(err){
            setErrorMessage(err.response.data.message);
        }
    }
    return(
        <>
            <div className="form-login mt-5 mb-5">
                <div className="form-login-container">
                    <h5 className='login-text-header text-center'>Login Account</h5>
                    <form className='form-login-form' autoComplete='off' onSubmit={onSubmitHandler}>
                        <div className="form-group">
                            <div className='error-message text-center'>{errorMessage ? `Error: ${errorMessage}` : ''}</div>
                        </div>
                        <div className='form-group'>
                            <input type='email' className='form-control' id='email' name='email' placeholder='Email' value={userInput.email} onChange={onChangeHandler}/>
                        </div>
                        <div className='form-group mt-4'>
                            <input type='password' className='form-control' id='password' name='password' placeholder='Password' value={userInput.password} onChange={onChangeHandler}/>
                        </div>
                        <div className='form-group text-right mt-4'>
                            <button type='submit' className='btn btn-center'>Login</button>
                        </div>
                    </form>
                </div>
            </div>
        </>
    )
}
export default Login;
