import {useContext, useState} from 'react';
import axios from 'axios';
import './PostCreate.css';
import { AppContext , actionpayloads} from '../../exportStore';

const PostCreate = () => {
    const {state, dispatch} = useContext(AppContext);
    const [posts, setPosts] = useState('');
    const [errorMessage, seterrorMessage] = useState('');

    const onChangeHandler = (e) => {
        setPosts(e.target.value);
    } 
    const onSubmitHandler = async (e) => {
        try{
            e.preventDefault();
            const option = {
                method: 'POST',
                url: '/api/v1/posts/create',
                data: posts
            }
            const response = await axios(option);
            //lấy tokens và tạo bài viết
            if(response.data.status === 'success'){
                setPosts('');
                dispatch(actionpayloads.createOnePost(response.data.data));
            }else{
                seterrorMessage(response.data.message);
            }
        }catch(err){
            seterrorMessage(err.message);
        }
    }
    return(
        <>
            {state.user &&
                <div class='container-p15 mt-3'>
                    <form class='form' onSubmit={onSubmitHandler}>
                        <div className='form-group'>
                            <div className='error-message'>{errorMessage ? `Lỗi: ${errorMessage}` : ''}</div>
                        </div>
                        <div class='form-group'>
                            <textarea className='form-control' id='content' name='content' rows='3' placeholder="What's happening?" onChange={onChangeHandler} value={posts}/>
                        </div>
                        <div class='form-group text-right'>
                            <button type='submit' className='btn'>
                            <i class="fab fa-twitter" style={{color:"#25dbf6"}}></i> Tweet</button>
                        </div>
                    </form>
                </div>
            }
        </>
    )
}
export default PostCreate;
