import {useContext, useState} from 'react';
import axios from 'axios';
import './PostCreate.css';
import { AppContext , actionpayloads} from '../../exportStore';
import { CKEditor } from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';

const PostCreate = () => {
    const {state, dispatch} = useContext(AppContext);
    const [posts, setPosts] = useState('');
    const [errorMessage, seterrorMessage] = useState('');

    const onChangeHandler = (e) => {
        setPosts(e.target.value);
    } 
    const onSubmitHandler = async (e) => {
        try{
            e.preventDefault();
            const token = localStorage.getItem('token');
            const option = {
                method: 'POST',
                url: 'api/v1/posts/create',
                headers: {
                    'Authorization': `Bearer ${token}`
                },
                data: {content: posts}
            }
            const response = await axios(option);
            const postsCreate = response.data.data;
            dispatch(actionpayloads.createOnePost(postsCreate));
            window.location.reload();
        }catch(err){
            seterrorMessage(err.response.data.message);
        }
    }
    return(
        <>
            {state.user &&
                <div class='container-p15 mt-3'>
                    {/* <form class='form' onSubmit={onSubmitHandler}>
                        <div className='form-group'>
                            <div className='error-message'>{errorMessage ? `Error: ${errorMessage}` : ''}</div>
                        </div>
                        <div class='form-group'>
                            <textarea className='form-control' id='content' name='content' rows='3' placeholder="What's happening?" onChange={onChangeHandler} value={posts}/>
                        </div>
                        <div class='form-group text-right'>
                            <button type='submit' className='btn-custom'>
                            <i class="fab fa-twitter" style={{color:"#25dbf6"}}></i> Tweet</button>
                        </div>
                    </form> */}
                    <form class='form' onSubmit={onSubmitHandler}>
                        <CKEditor
                            editor={ ClassicEditor }
                            data={posts}
                            onChange={ ( event, editor ) => {
                                const data = editor.getData();
                                const post = data.substring(3, data.length-4);
                                // onChangeHandler({target: {value: post}});
                            } }
                            onBlur={ ( event, editor ) => {
                                console.log( 'Blur.', editor );
                            } }
                            onFocus={ ( event, editor ) => {
                                console.log( 'Focus.', editor );
                            } }
                        />
                        <div class='form-group text-right'>
                            <button type='submit' className='btn-custom'>
                            <i class="fab fa-twitter" style={{color:"#25dbf6"}}></i> Tweet</button>
                        </div>
                    </form>
                </div>
            }
        </>
    )
}
export default PostCreate;
