import {useReducer, useCallback} from 'react';
import {Routes, Route} from 'react-router-dom'
import axios from 'axios';
import './components/index.js';
import {Header, Home, Footer, Login, Register, 
        PageNotFound, AppContext, reducer, actionpayloads
} from './exportStore';

function App() {
  const [state, dispatch] = useReducer(reducer, actionpayloads.initialState);
  //check current user
  const checkCurrentUser = useCallback(async () => {
    try{
      const token = localStorage.getItem('token');
      const option = {
        method: 'GET',
        url: '/api/v1/users/',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      }
      const response = await axios(option);
      if(response.data.data.user){
        const {username} = response.data.data.user;
        dispatch(actionpayloads.currentUser(username));
      }
    }catch(err){

    }
  })
  return (
    <AppContext.Provider value={{state, dispatch}}>
      <div className="app">
        <Header/>
        <Routes>
          <Route path="/" element={<Home/>}/>
          <Route path="/login" element={<Login/>}/>
          <Route path="/register" element={<Register/>}/>
          <Route path="*" element={<PageNotFound/>}/>
        </Routes>
        <Footer/>
      </div>
    </AppContext.Provider>
  );
}

export default App;
