import {Link} from 'react-router-dom';
import {useContext} from 'react';
import {AppContext} from '../../exportStore';
import './Header.css'

const Header = () => {
    const {state} = useContext(AppContext);
    const logoutAccount = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        window.location.href = '/';
    }
    return (
        <>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
            <Link className="navbar-brand text-bold" to="/">
                <i class="fab fa-twitter" style={{color:"#25dbf6"}}></i> Twitter
            </Link>
            <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav ml-auto">
                {state.user ? 
                    <>
                        <li className="nav-item">
                            <Link className="nav-link user-active" to="#">Hello, {state.user}</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="#"><i class="fas fa-sign-out-alt" style={{color:"#25dbf6"}}></i> Sign out</Link>
                        </li>
                    </> : 
                    <>
                        <li className="nav-item active">
                            <Link className="nav-link" to="/login"><i class="fas fa-sign-in-alt" style={{color:"#25dbf6"}}></i> Login</Link>
                        </li>
                        <li className="nav-item">
                            <Link className="nav-link" to="/register"><i class="fas fa-registered" style={{color:"#25dbf6"}}></i> Register</Link>
                        </li>
                    </>
                }
            </ul>
            </div>
        </nav>
        </>
    )
}
export default Header;
