import { useContext, useState } from 'react';
import {Link} from 'react-router-dom';
import {PostUpdate, AppContext} from '../../../exportStore';
const PostItem = ({post}) => {
    const {state} = useContext(AppContext);
    const [openEditForm, setOpenEditForm] = useState(false);
    const [openDeleteForm, setOpenDeleteForm] = useState(false);

    function convertDate(date){
        let newDate = new Date(date);
        let day = newDate.getDate();
        let month = newDate.getMonth() + 1;
        let year = newDate.getFullYear();
        if(day < 10){
            day = '0' + day;
        }
        if(month < 10){
            month = '0' + month;
        }
        return `${day}/${month}/${year}`
    }
    return (
        <>
            <li className='content-item mb-3'>
                <div className='content-top'>
                    <p className='text-content color-white'>{post.content}</p>
                    <div className='content-info d-flex justify-content-between mt-2'>
                        <div className='content-info-left'>
                            <Link className='text-content-left-link mr-3 text-primary text-underline' to='#'>by: {post.author.username}</Link>
                            <span className='text-content-left-date color-white'>Date: <span className='createdDate'>{convertDate(post.createdAt)}</span></span>
                        </div>
                        <div className='content-info-right d-flex'>
                            {post.author.username === state.user &&
                                <>
                                    <div className='action-content-right'>
                                        <span className='action-content-right-link text-primary text-underline cr-pt' onClick={() => setOpenEditForm(true)}>Edit</span>
                                        <span className='action-content-right-link ml-2 text-underline text-danger cr-pt' onClick={() => setOpenDeleteForm(false)}>Delete</span>
                                    </div>
                                    {openDeleteForm && 
                                        <div className='confirm-content-right'>
                                            <Link className='confirm-content-right-link text-warning text-underline' to='#'>Are you sure?</Link>
                                            <Link className='confirm-content-right-link mx-2 text-danger text-underline' to='#'>Yes</Link>
                                            <Link className='confirm-content-right-link text-success text-underline' to='#'>No</Link>
                                        </div>
                                    }
                                </>
                            }
                        </div>
                    </div>
                </div>
                <div className='post-update none'>
                    <PostUpdate/>
                </div>
            </li>
        </>
    )
}
export default PostItem;
