import { useContext, useState } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {AppContext} from '../../../exportStore';
const PostItem = ({post}) => {
    const {state, dispatch} = useContext(AppContext);
    const [openEditForm, setOpenEditForm] = useState(false);
    const [openDeleteForm, setOpenDeleteForm] = useState(false);
    function convertDate(date){
        let newDate = new Date(date);
        let day = newDate.getDate();
        let month = newDate.getMonth() + 1;
        let year = newDate.getFullYear();
        if(day < 10){
            day = '0' + day;
        }
        if(month < 10){
            month = '0' + month;
        }
        return `${day}/${month}/${year}`
    }
    function openEdit (id) {
        let postFilter = state.posts.find(post => post._id === id);
        if(openEditForm){
            setOpenEditForm(false);
        }else{
            setOpenEditForm(true);
        }
    }
    function deleteConfirm () {
        if(openDeleteForm){
            setOpenDeleteForm(false);
        }else{
            setOpenDeleteForm(true);
        }
    }
    
    return (
        <>
            <li className='content-item mb-3'>
                <div className='content-top'>
                    <p className='text-content color-white'>{post.content}</p>
                    <div className='content-info d-flex justify-content-between mt-2'>
                        <div className='content-info-left'>
                            <Link className='text-content-left-link mr-3 text-primary text-underline' to='#'>by: {post.author.username}</Link>
                            <span className='text-content-left-date color-white'>Date: <span className='createdDate'>{convertDate(post.createdAt)}</span></span>
                        </div>
                        <div className='content-info-right d-flex'>
                            {post.author.username === state.user &&
                                <>
                                    {!openDeleteForm &&
                                        <div className='action-content-right'>
                                            <span className='action-content-right-link text-primary text-underline cr-pt' onClick={() => openEdit(post._id)}>Edit</span>
                                            <span className='action-content-right-link ml-2 text-underline text-danger cr-pt' onClick={deleteConfirm}>Delete</span>
                                        </div>
                                    }
                                    {openDeleteForm && 
                                        <div className='confirm-content-right'>
                                            <Link className='confirm-content-right-link text-warning text-underline' to='#'>Are you sure?</Link>
                                            <Link className='confirm-content-right-link mx-2 text-danger text-underline' to='#'>Yes</Link>
                                            <span className='confirm-content-right-link text-success text-underline cr-pt' onClick={deleteConfirm}>No</span>
                                        </div>
                                    }
                                </>
                            }
                        </div>
                    </div>
                </div>
                {openEditForm &&
                    <div className='content-bottom mt-3'>
                        <form className='form-update'>
                            <div className='form-group'>
                                <textarea className='form-control content-update' id='content-update' name='content-update' rows='3'/>
                            </div>
                            <div className='form-group text-right'>
                                <button type='submit' className='btn mr-2'>
                                    <i class="fas fa-edit" style={{color:"#25dbf6"}}></i> Update</button>
                                <span className='btn' onClick={openEdit}>
                                <i class="fas fa-times-circle" style={{color:"#25dbf6"}}></i> Cancel</span>
                            </div>
                        </form>
                    </div>
                }
            </li>
        </>
    )
}
export default PostItem;
