import { useContext, useState } from 'react';
import axios from 'axios';
import {Link} from 'react-router-dom';
import {actionpayloads, AppContext} from '../../../exportStore';
const PostItem = ({post}) => {
    const {state, dispatch} = useContext(AppContext);
    const [openEditForm, setOpenEditForm] = useState(false);
    const [openDeleteForm, setOpenDeleteForm] = useState(false);
    const [postInput, setPostInput] = useState(post);
    const [erroMessage, setErroMessage] = useState(null);
    function convertDate(date){
        let newDate = new Date(date);
        let day = newDate.getDate();
        let month = newDate.getMonth() + 1;
        let year = newDate.getFullYear();
        if(day < 10){
            day = '0' + day;
        }
        if(month < 10){
            month = '0' + month;
        }
        return `${day}/${month}/${year}`
    }
    function openEdit (id) {
        let postFilter = state.posts.find(post => post._id === id);
        if(openEditForm){
            setOpenEditForm(false);
        }else{
            setOpenEditForm(true);
            setPostInput(postFilter);
        }
    }
    function deleteConfirm () {
        if(openDeleteForm){
            setOpenDeleteForm(false);
        }else{
            setOpenDeleteForm(true);
        }
    }
    const onChangeHandler = (e) => {
        setPostInput(e.target.value);
    }
    const onSubmitUpdateHandler = async (e) => {
        try{
            e.preventDefault();
            setOpenEditForm(false);
            let token = localStorage.getItem('token');
            const option = {
                method: 'PUT',
                url: `api/v1/posts/update/${post._id}`,
                headers: {
                    'Authorization': `Bearer ${token}`
                },
                data: {...post, content: postInput}
            }
            const response = await axios(option);
            const posts = response.data.data;
            dispatch(actionpayloads.updateOnePost(posts));
            window.location.reload();
        }catch(err){
            setErroMessage(err.response.data.message);
        }
    }
    const deletePostHandler = async (id) => {
        try{
            const token = localStorage.getItem('token');
            const option = {
                method: 'DELETE',
                url: `api/v1/posts/delete/${id}`,
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            };
            const response = await axios(option);
            const posts = response.data.data;
            dispatch(actionpayloads.deleteOnePost(posts._id));
        }catch(err){
            setErroMessage(err.response.data.message);
        }

    }
    
    return (
        <>
            <li className='content-item mb-3'>
                <div className='content-top'>
                    <div className='content-top-left'>
                        {erroMessage ? <p className='text-danger'>{erroMessage}</p> : null}
                    </div>
                    <p className='text-content color-white'>{post.content}</p>
                    <div className='content-info d-flex flex-wrap justify-content-between mt-4'>
                        <div className='content-info-left'>
                            <Link className='text-underline mr-3 text-primary' to='#'>by: {post.author.username}</Link>
                            <span className="badge badge-light">Date: {convertDate(post.createdAt)}</span>
                        </div>
                        <div className='content-info-right d-flex'>
                            {post.author.username === state.user &&
                                <>
                                    {!openDeleteForm &&
                                        <div className='action-content-right'>
                                            <span className='btn btn-primary mr-2' onClick={() => openEdit(post._id)}>Edit</span>
                                            <span className='btn btn-danger' onClick={deleteConfirm}>Delete</span>
                                        </div>
                                    }
                                    {openDeleteForm && 
                                        <div className='confirm-content-right'>
                                            <Link className='text-warning text-underline' to='#'>Are you sure?</Link>
                                            <span className='mx-2 btn btn-danger' onClick={() => deletePostHandler(post._id)}>Yes</span>
                                            <span className='btn btn-primary' onClick={deleteConfirm}>No</span>
                                        </div>
                                    }
                                </>
                            }
                        </div>
                    </div>
                </div>
                {openEditForm &&
                    <div className='content-bottom mt-3'>
                        <form className='form-update' onSubmit={onSubmitUpdateHandler}>
                            <div className='form-group'>
                                <textarea className='form-control content-update' id='content-update' name='content-update' rows='3' value={postInput.content} onChange={onChangeHandler}/>
                            </div>
                            <div className='form-group text-right'>
                                <button type='submit' className='btn btn-warning mr-2'>
                                    <i class="fas fa-edit" style={{color:"#000"}}></i> Update</button>
                                <span className='btn btn-primary' onClick={openEdit}>
                                    <i class="fas fa-times-circle" style={{color:"#000"}}></i> Cancel
                                </span>
                            </div>
                        </form>
                    </div>
                }
            </li>
        </>
    )
}
export default PostItem;
