import './Register.css';
import {useState, useContext} from 'react';
import {useNavigate} from 'react-router-dom'
import {AppContext} from '../../exportStore';

const Register = () => {
    const {dispatch} = useContext(AppContext);
    const [userInput, setUserInput] = useState({username: "", email: "", password: ""});
    const [errorMessage, setErrorMessage] = useState(null);
    const history = useNavigate();
    return (
        <>
            <div className="form-login mt-5 mb-5">
                <div className="form-login-container">
                    <h5 className='login-text-header text-center'>Register Account</h5>
                    <form className='form-login-form' autocomplete='off'>
                        <div className="form-group">
                            <div className='error-message'></div>
                        </div>
                        <div className='form-group'>
                            <input type='text' className='form-control' id='username-resgister' name='username-resgister' placeholder='Name'/>
                        </div>
                        <div className='form-group mt-4'>
                            <input type='email' className='form-control' id='email-resgister' name='email-resgister' placeholder='Email'/>
                        </div>
                        <div className='form-group mt-4'>
                            <input type='password' className='form-control' id='password-register' name='password-register' placeholder='Password'/>
                        </div>
                        <div className='form-group text-right mt-4'>
                            <button type='submit' className='btn mt-2 btn-center'>Register</button>
                        </div>
                    </form>
                </div>
            </div>
        </>
    )
}
export default Register;
