/* eslint-disable react-hooks/exhaustive-deps */
import axios from 'axios';
import {useEffect, useCallback, useContext} from 'react';
import {PostItem, AppContext, actionpayloads} from '../../../exportStore';
import '../Posts.css';

const PostContainer = () => {
    const {state, dispatch} = useContext(AppContext);
    const option = {
        method: 'GET',
        url: `api/v1/posts`
    }
    const getAllPost = useCallback(async () => {
        try {
            const res = await axios(option);
            const posts = res.data.data;
            dispatch(actionpayloads.getAllPost(posts));
        } catch (error) {
            console.log(error);
        }
    },[]);
    useEffect(() => {
        getAllPost();
    },[getAllPost]);
    return (
        <>
            <div className='container-p15 mt-3 mh-60vh'>
                <ul className='content-cotainer-list'>
                    {state.posts && state.posts.map((post, index) => (
                        <PostItem key={index} post={post}/>
                    ))}
                </ul>
            </div>
        </>
    )
}
export default PostContainer;
